package com.osard.alipaylibrary.result;import android.text.TextUtils;import com.alibaba.fastjson.JSON;import com.osard.alipaylibrary.enumeration.AliPayResultStatus;import com.osard.alipaylibrary.enumeration.AliPublicCodeErr;import com.osard.alipaylibrary.result.entity.AliPayResultParam;import org.json.JSONObject;import java.util.Map;public class AliPayResult {    /**     * 参考 {@link AliPayResultStatus}     */    private AliPayResultStatus resultStatus;    private String result;    private String memo;    /**     * 参考 {@link AliPublicCodeErr}     */    private AliPublicCodeErr resultCode;    /**     * {@link #result} 对象表现形式     */    private AliPayResultParam aliPayResultParam;    public AliPayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = AliPayResultStatus.getType(rawResult.get(key).trim());            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }        if (TextUtils.isEmpty(result)) {            return;        }        try {            aliPayResultParam = JSON.parseObject(result, AliPayResultParam.class);            if (null != aliPayResultParam && null != aliPayResultParam.getAlipayTradeAppPayResponse()) {                resultCode = AliPublicCodeErr.getType(aliPayResultParam.getAlipayTradeAppPayResponse().getCode().trim());            }        } catch (Exception ignore) {        }    }    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}";    }    /**     * @return the resultStatus     */    public AliPayResultStatus getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    public String getMemo() {        return memo;    }    /**     * @return the result     */    public String getResult() {        return result;    }    /**     * @return result 下的 alipay_trade_app_pay_response 字段下的 code     */    public AliPublicCodeErr getResultCode() {        return resultCode;    }    public AliPayResultParam getAliPayResultParam() {        return aliPayResultParam;    }}